import {Container,Row,Col,Card} from 'react-bootstrap';
export default function Carta(props){
    var pos=0;
    return (
        <Card style={{ width: '10rem',height:'15rem',padding:'0.125rem' }} className="justify-content-md-center">
        <Card.Body>
          <Card.Title className={props.index<=1?'text-black':'text-danger'}>{props.num}</Card.Title>
            <Container>
              {
                Array.apply(null, { length: (props.num%2==0)?(props.num/2):(props.num/2+1) }).map((e, i) => {
                  return <Row key={i} className={props.index<=1?'text-black':'text-danger'}>
                     <Col><h3>{props.palo}</h3></Col>
                     {
                     ((pos=pos+2)<=props.num)
                     ?<Col><h3>{props.palo}</h3></Col>
                     :<span></span>
                    }
                   </Row>
                   })
              }
            </Container>
        </Card.Body>
      </Card>
      );
}
